import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [users, setUsers] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');

  useEffect(() => {
    fetchUsers();
  }, []);

  // Fetch all registered users from backend
  const fetchUsers = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/users');  // Make sure the backend is running
      setUsers(res.data);  // Set the users array from the backend response
    } catch (error) {
      console.error(error);
      setErrorMessage('Failed to fetch users. Please check the server.');
    }
  };

  // Handle form submission for registering a new user
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/register', { username, email });
      fetchUsers();  // Refresh the list of users after successful registration
      setUsername('');  // Clear input fields
      setEmail('');
    } catch (error) {
      console.error(error);
      setErrorMessage('Failed to register user. Please check the server.');
    }
  };

  return (
    <div className="container">
      <h1>User Registration</h1>
      {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <button type="submit">Register</button>
      </form>

      <h2>Registered Users</h2>
      <ul>
        {users.map((user) => (
          <li key={user._id}>
            {user.username} ({user.email})
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
